import React, { Component } from 'react';
import randomColor from 'randomcolor';
//import TagCloud from 'react-tag-cloud';
import { TagCloud } from 'react-tagcloud'
//import CloudItem from './CloudItem';

const styles = {
    large: {
      fontSize: 60,
      fontWeight: 'bold'
    },
    small: {
      opacity: 0.7,
      fontSize: 16
    }
  };


const data = [
  { value: 'JavaScript', count: 38 },
  { value: 'React', count: 30 },
  { value: 'Nodejs', count: 28 },
  { value: 'Express.js', count: 25 },
  { value: 'HTML5', count: 33 },
  { value: 'MongoDB', count: 18 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 50 },
]

const data2 = [
  { value: 'JavaScript', count: 38 },
  { value: 'React', count: 30 },
  { value: 'Nodejs', count: 28 },
  { value: 'Express.js', count: 25 },
  { value: 'HTML5', count: 33 },
  { value: 'MongoDB', count: 18 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 20 },
  { value: 'CSS3', count: 50 },
]

class WordCloud extends Component {
  render() {
    return (
        <div>
          {/* <div>
            Test
          </div> */}
        <TagCloud 
        className='tag-cloud'
        style={{
          fontFamily: 'sans-serif',
          //fontSize: () => Math.round(Math.random() * 50) + 16,
          fontSize: 30,
          color: () => randomColor({
            hue: 'blue'
          }),
          padding: 5,
          flex: 1
        }}>
        <div
          style={{
            fontFamily: 'serif',
            fontSize: 40,
            fontStyle: 'italic',
            fontWeight: 'bold',
            color: randomColor()
          }}>Futurama{console.log('rendering wordcloud')}</div>

        <div style={styles.large}>Transformers</div>
        <div style={styles.large}>Simpsons</div>
        <div style={styles.large}>Dragon Ball</div>
        <div style={styles.large}>Rick & Morty</div>
        <div style={{fontFamily: 'courier'}}>He man</div>
        <div style={{fontSize: 30}}>World trigger</div>
        <div style={{fontStyle: 'italic'}}>Avengers</div>
        <div style={{fontWeight: 200}}>Family Guy</div>
        <div style={{color: 'green'}}>American Dad</div>
        <div className="tag-item-wrapper">
          <div>
            Hover Me Please!
          </div>
          <div className="tag-item-tooltip">
            HOVERED!
          </div>
        </div>
        <div>Gobots</div>
        <div>Thundercats</div>
        <div>M.A.S.K.</div>
        <div>GI Joe</div>
        <div>Inspector Gadget</div>
        <div>Bugs Bunny</div>
        <div>Tom & Jerry</div>
        <div>Cowboy Bebop</div>
        <div>Evangelion</div>
        <div>Bleach</div>
        <div>GITS</div>
        <div>Pokemon</div>
        <div>She Ra</div>
        <div>Fullmetal Alchemist</div>
        <div>Gundam</div>
        <div>Uni Taisen</div>
        <div>Pinky and the Brain</div>
        <div>Bobs Burgers</div>
        <div style={styles.small}>Dino Riders</div>
        <div style={styles.small}>Silverhawks</div>
        <div style={styles.small}>Bravestar</div>
        <div style={styles.small}>Starcom</div>
        <div style={styles.small}>Cops</div>
        <div style={styles.small}>Alfred J. Kwak</div>
        <div style={styles.small}>Dr Snuggles</div>
      </TagCloud>
      <div style={{'display':'flex', 'flex-direction':'row'}}>
        <div>
        <TagCloud
          style={{width: 200, height: 50}}
          minSize={12}
          maxSize={35}
          tags={data}
          //onClick={tag => alert(`'${tag.value}' was selected!`)}
        />
        </div>
        <div>           </div>
        <div>
        <TagCloud
          style={{width: 200, height: 50}}
          minSize={12}
          maxSize={35}
          tags={data2}
          //onClick={tag => alert(`'${tag.value}' was selected!`)}
        />
        </div>
      </div>
    </div>
    );
  }
}

export default WordCloud;
